package com.Syntax.class1;

public class class1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Write a Java program to add, subtract, multiply and 
		 * divide and square 2 decimal values. Your program should say. 
		 * “The _______ of 2 numbers ___ and ___ is equal to _____”
		 * 
		 */
		double a=10.5;
		double b=20.5;
	    double sum=a+b;
	    double sub=a-b;
	    double mul=a*b;
	    double div= a/b;
	    System.out.println( " the sum of two numbers " +a+ " and " +b+ " is equal to " +sum);
	    System.out.println( " the sub of two numbers " +a+ " and " +b+ " is equal to " +sub);
	    System.out.println( " the mul of two numbers " +a+ " and " +b+ " is equal to " +mul);
	    System.out.println( " the div of two numbers " +a+ " and " +b+ " is equal to " +div);
	    System.out.println( " the square of two numbers " +a+ " and " +b+ " is equal to " +(a*b));
	    
	    
	    
	    
	    
	    
	
		
				

	}

}
